package LinkedList;

public class Item {

	// Listenin her bir elemanýnýn deðerini ve
	// kendisinden sonra gelecek elemanýn referans adresini saklayacak olan veri alanlarý
	int _value;
	Item _next;

	// Yapýcý metod
	public Item(int value, Item next) {
		// TODO Auto-generated constructor stub
		this._value = value;
		this._next = next;
	}

}
